import React from 'react';
import './App.css';

function App() {
  id = 1;
  state = {
    username: '',
    password: '',
    list: []
  }
  handleChange = e => {
    const {value, name}  = e.target;
    this.setState({
      [name] : value
    })
  }

  handleInsert = () =>{
    const {username, password, list} = this.state;
    this.setState({
      username : '',
      password: '',
      list : list.concat({id: this.id, username, password})
    });
    this.id += 1;
  };
  render() 
    const {username, password, list} = this.state;
    return (
      <div>
        <input name="username" value={username} onChange={this.handleChange} />
        <input name="password" value={password} onChange={this.handleChange} />
        <button onclick={this.handleInsert}>추가하기</button>
        {list.map((item) => (
          <li key={item.id}>
            {item.username} : ({item.password})
          </li>
        ))}
      </div>
    );


}

export default App;
